import { useState } from 'react'; //共享状态的useState 方法 rect hooks

export default function useStats(url) {
  // console.log(url);
  // from input 双向绑定新写法 
  const [stats, setStats] = useState(); 
  const [Loading ,setLoading]=useState(true);
  return {
    stats
  }
}
